var path = require('path')
var spawn = require('child_process').spawn

var babelNode = path.resolve(process.cwd(), 'node_modules/.bin/babel-node')
var knex = path.resolve(process.cwd(), 'node_modules/.bin/knex')

var argv = [knex].concat(process.argv.slice(2))
spawn(babelNode, argv, { stdio: 'inherit' })
  .on('exit', process.exit)
